<?phprequire("config.php");$query=mysql_query("SELECT uid,name,level FROM users ORDER BY level DESC,time");$x=1;?><table border="1"><tr><td>Rank</td><td>Name</td><td>Points</td></tr><?phpwhile($temp=mysql_fetch_array($query)){?><tr><td><?php echo $x; ?></td><td><?php echo $temp['name']; ?></td><td><?php echo $temp['level']; ?></td></tr><?phpif($x==15)break;$x++;}include("close.php");?></table>